mapper.update:
update filmbox_post set member_id = #{member_id}, 
						post_content = #{post_content}, 
						movieNm = #{movieNm}, 
						image = #{image}
						where post_no= #{post_no}

mapper.select:
select count(*)
from filmbox_post
where member_id = #{member_id}

SELECT post_no, member_id, post_content, movieNm,image, post_date
FROM filmbox_post
where member_id = #{member_id}
