mapper.insert:
insert into filmbox_member ( member_id
						, member_name
						, member_pwd
						, member_phone
						, email )
select #{member_id}
 		, #{member_name}
 		, #{member_pwd}
 		, #{member_phone}
 		, #{email} from dual
where not exists ( select member_id from filmbox_member
					 where member_id=#{member_id} )

		insert into filmbox_member  ( member_id
								, member_name
								, member_pwd
								, member_phone
								, email
								, point ) 
         select  #{member_id}
         		 , #{member_name}
         		 , #{member_pwd}
         		 , #{member_phone}
         		 , #{email}
         		 , '3000' from dual
         where not exists ( select member_id from filmbox_member
         					 where member_id=#{member_id} )

mapper.select:
select count(*)
from filmbox_member
where member_id=#{member_id}
