mapper.insert:
insert into filmbox_member ( member_id
						, member_name
						, member_pwd
						, member_phone
						, email
						, point
						, login_type )
select #{member_id}
 		, #{member_name}
 		, #{member_pwd}
 		, #{member_phone}
 		, #{email}
 		, 0
 		, #{login_type}
where not exists ( select member_id from filmbox_member
					 where member_id=#{member_id} )

mapper.select:
select count(*)
from filmbox_member
where member_id=#{member_id}

select count(*)
from filmbox_member
where member_id=#{member_id} and member_pwd=#{member_pwd}

select member_id
from filmbox_member
where email=#{email}

mapper.update:
update filmbox_member
set
	member_pwd=#{member_pwd}
where member_id=#{member_id}

mapper.select:
select member_id
from filmbox_member
where member_id=#{member_id} and member_pwd=#{member_pwd}

select  member_id, member_name, member_pwd, member_phone, email, point, login_type 
from filmbox_member
where member_id=#{member_id}

mapper.update:
update filmbox_member
set  member_id = #{member_id}
	, member_name = #{member_name}
	, member_pwd = #{member_pwd}
	, member_phone = #{member_phone}
	, email = #{email}
where member_id=#{member_id}

mapper.select:
select count(*)
from filmbox_member
where email=#{email}
